import { createSlice } from "@reduxjs/toolkit";
import NGO from "../";

// step 3 for redux - create slices for each components 
const NgoSlice = createSlice({

    name: 'ngo',

    initialState: {
        // empState: {
        //     eid: 101,
        //     firstName: 'Sonu',
        //     salary: 10.5
        // }

        ngoState: new NGO(),
        ngoList: []


    },

    reducers: {

        getNgoById: (state, action) => {
            console.log('EmpSlice reducers getNgoById');
            state.ngoState = action.payload;
        },

        // getAllEmps: (state, action) => {
        //     console.log('EmpSlice reducers getAllEmps');
        //     state.empList = action.payload;
        // }

        // more methods will be added 
    }
});

export const { getNgoById} = NgoSlice.actions;

export default NgoSlice.reducer;
